class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if n == 1:
            for i in range(9, -1, -1):
                if i % k == 0:
                    return str(i)            
        if k == 1 or k == 3 or k == 9:
            return "9" * n
        elif k == 2 or k == 5:
            if k == 2:
                d = "8"
            elif k == 5:
                d = "5"
            if n == 1:
                return d
            else:
                return d + "9" * (n-2) + d
        elif k == 4:
            if n <= 4:
                return "8" * n
            else:
                return "88" + "9" * (n-4) + "88"
        elif k == 8:
            if n <= 6:
                return "8" * n
            else:
                return "888" + "9" * (n-6) + "888"
        elif k == 7:
            s = ["7", "77", "5", "77", "7", "", "4", "44", "6", "44", "4", ""]
            v = n % 12
            l = (n - len(s[v-1]))//2
            return "9" * l + s[v-1] + "9" * l

            # if n % 6 == 0:
            #     return "9" * n
