            # pass
        elif k == 6:
            if n == 2:
                return "66"
            if n % 2 == 1:
                l = (n - 3) // 2
                return "8"+ "9"*l + "8"+ "9"*l + "8"
            else:
                l = (n - 4) // 2
                return "8"+ "9"*l + "77"+ "9"*l + "8"
