class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1) {
            return string(n, '9');
        }
        if (k == 2) {
            if (n == 1) {
                return "8";
            }
            return "8" + string(n - 2, '9') + "8";
        }
        if (k == 3) {
            return string(n, '9');
        }
        if (k == 4) {
            if (n == 1) {
                return "8";
            }
            if (n == 2) {
                return "88";
            }
            if (n == 3) {
                return "888";
            }
            return "88" + string(n - 4, '9') + "88";
        }
        if (k == 5) {
            if (n == 1) {
                return "5";
            }
            return "5" + string(n - 2, '9') + "5";
        }
        if (k == 6) {
            if (n == 1) {
                return "6";
            }
            if (n == 2) {
                return "66";
            }
            if (n % 2 == 0) {
                return "8" + string((n / 2) - 2, '9') + "77" + string((n / 2) - 2, '9') + "8";
            }
            return "8" + string((n - 2) / 2, '9') + "8" + string((n - 2) / 2, '9') + "8";
        }
        if (k == 7) {
            vector<string> v = {"7", "77", "5", "77", "7", "9", "4", "44", "6", "44", "4", "9"};
            string cent = v[(n - 1) % 12];

            int len = (n - 1) / 2;

            if (n % 6 == 0) {
                return string(n, '9');
            }
            return string(len, '9') + cent + string(len, '9');
        }
        if (k == 8) {
            if (n <= 6) {
                return string(n, '8');
            }

            return "888" + string(n - 6, '9') + "888";
        }
        if (k == 9) {
            return string(n, '9');
        }

        return "";
    }
};
