class Solution {
public:
    string largestPalindrome(int n, int k) {
        string s(n,'9');
        switch(k){
            case 1:break;
            case 2:{
                s[0]=s[n-1]='8';
                break;
            }
            case 3:break;
            case 4:{
                if(n==1)s[0]='8';
                else if(n==2)s[0]=s[1]='8';
                else s[0]=s[1]=s[n-1]=s[n-2]='8';
                break;
            }
            case 5:{
                s[0]=s[n-1]='5';
                break;
            }
            case 6:{
                if(n==1)s[0]='6';
                else if(n==2)s[0]=s[1]='6';
                else if(n%2==1){
                    s[0]=s[n-1]='8';
                    s[n/2]='8';
                }
                else {
                    s[0]=s[n-1]='8';
                    s[n/2-1]=s[n/2]='8=7';
                }
                break;
            }
            case 7:{
                int ys=n%12;
                switch(ys){
                    case 0:break;
                    case 1:{
                        s[n/2]='7';break;
                    }
                    case 2:{
                        s[n/2-1]=s[n/2]='7';break;
                    }
                    case 3:{
                        s[n/2]='5';break;
                    }
                    case 4:{
                        s[n/2-1]=s[n/2]='7';break;
                    }
                    case 5:{
                        s[n/2]='7';break;
                    }
                    case 6:break;
                    case 7:{
                        s[n/2]='4';break;
                    }
                    case 8:{
                        s[n/2-1]=s[n/2]='4';break;
                    }
                    case 9:{
                        s[n/2]='6';break;
                    }
                    case 10:{
                        s[n/2-1]=s[n/2]='4';break;
                    }
                    case 11:{
                        s[n/2]='4';break;
                    }
                }
                break;
            }
            case 8:{
                s[0]=s[n-1]='8';
                if(n>=3)s[1]=s[n-2]='8';
                if(n>=5)s[2]=s[n-3]='8';
            }
        }
        return s;
    }
};
