class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if(k==1 or k==3 or k==9):
            return '9'*n
        if(k==2):
            if(n==1):
                return '8'
            else:
                return '8'+'9'*(n-2)+'8'
        if(k==4):
            if(n==1):
                return '8'
            if(n==2):
                return '88'
            if(n==3):
                return '888'
            return '88'+'9'*(n-4)+'88'
        if(k==5):
            if(n==1):
                return '5'
            return '5'+'9'*(n-2)+'5'
        if(k==6):
            if(n==1):
                return '6'
            if(n==2):
                return '66'
            res=['']*n
            res[0]=res[n-1]='8'
            left=1
            right=n-2
            while(left<=right):
                if(left+1==right):
                    res[left]='7'
                    res[right]='7'
                elif(left==right):
                    res[left]='8'
                else:
                    res[left]=res[right]='9'
                left+=1
                right-=1
            return ''.join(res)
        if(k==8):
            if(n<=6):
                return '8'*n
            else:
                return '888'+'9'*(n-6)+'888'
        def isdivby7(s):
            rem=0
            for i in s:
                rem=(rem*10+int(i))%7
            return rem==0
        res=['9']*n
        if(n==1):
            return '7'
        if(n==2):
            return '77'
        if(n%2==0):
            ind1,ind2=(n-1)//2,n//2
            for i in [99,88,77,66,55,44,33,22,11,00]:
                s=str(i)
                res[ind1]=s[0]
                res[ind2]=s[1]
                if(isdivby7(res)):
                    return ''.join(res)
        else:
            ind=n//2
            for i in range(9,-1,-1):
                s=str(i)
                res[ind]=s[0]
                if(isdivby7(res)):
                    return ''.join(res)
