import sys
sys.set_int_max_str_digits(10**6)
class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        ary=["9" for _ in range(n)]
        def is_palindrome(num):
            return str(num) == str(num)[::-1]
        if n<=8:
            maxx=int("".join(ary))
            for num in range(maxx, 0, -1):
                if is_palindrome(num) and num % k == 0:
                    return str(num)


        if k in {1,3,9}:
            return "".join(ary)

        if k==2:
            ary[0]="8"
            ary[-1]="8"
            return "".join(ary)
        if k==4:
            ary[0]="8"
            ary[1]="8"
            ary[-1]="8"
            ary[-2]="8"
            return "".join(ary)

        if k==5:
            ary[0]="5"
            ary[-1]="5"
            return "".join(ary)

        if k==6:
            ary[0]="8"
            ary[-1]="8"
            if n&1:
                ary[n//2]="8"
                return "".join(ary)
            else:
                ary[n//2]="7"
                ary[n//2-1]="7"
                return "".join(ary)

        if k==8:
            ary[0]="8"
            ary[1]="8"
            ary[2]="8"
            ary[-1]="8"
            ary[-2]="8"
            ary[-3]="8"
            return "".join(ary)

        if k==7:
            if n&1:
                l=r=n//2
            else:
                l=n//2-1
                r=n//2
            while True:
                tmps="".join(ary)
                tmp=int(tmps)
                if tmp%7==0:
                    return tmps
                digit=int(ary[l])
                if digit==0:
                    ary[l]=ary[r]="9"
                    l-=1
                    r+=1
                digit=int(ary[l])
                dm1=digit-1
                dm1s=str(dm1)
                ary[l]=ary[r]=dm1s
