#define FOR(i, a, b) for (int i = (a); i < (b); i++)
#define REP(i, n) for (int i = 0; i < (n); i++)

class Solution {
public:
  string largestPalindrome(int n, int k) {
    vector<int> ten(n), c(n);
    string a(n, '9');
    int m = (n+1)/2, s = 0;
    ten[0] = 1;
    FOR(i, 1, n)
      ten[i] = ten[i-1]*10%k;
    if (k%2==0)
      a[0] = '8';
    if (k==5)
      a[0] = '5';
    else if (k==4&&m>=2)
      a[0] = '8', a[1] = '8';
    else if (k==8&&m>=3)
      a[0] = '8', a[1] = '8', a[2]='8';
    REP(i, m) {
      c[i] = (ten[i] + (2*i+1 < n ? ten[n-1-i] : 0))%k;
      s = (s+c[i]*(a[i]-'0'))%k;
    }
    while (s) {
      int i = m-1;
      for(;;i--) {
        if (a[i] == '0') {
          a[i] = '9';
          s += 9*c[i];
          continue;
        }
        a[i]--;
        s -= c[i];
        break;
      }
      s %= k;
    }
    REP(i, m)
      a[n-1-i] = a[i];
    return a;
  }
};
