class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) {
            return string(n, '9');
        }
        if (k == 2) {
            if (n == 1) {
                return "8";
            }
            if (n == 2) {
                return "88";
            }
            return "8" + string(n - 2, '9') + "8";
        }
        if (k == 4) {
            if (n == 1) {
                return "8";
            }
            if (n == 2) {
                return "88";
            };
            if (n == 3) {
                return "888";
            }
            if (n == 4) {
                return "8888";
            }
            return "88" + string(n - 4, '9') + "88";
        }
        if (k == 5) {
            if (n == 1) {
                return "5";
            }
            if (n == 2) {
                return "55";
            }
            return "5" + string(n - 2, '9') + "5";
        }
        if (k == 6) {
            if (n == 1) {
                return "6";
            }
            if (n == 2) {
                return "66";
            }
            if (n == 3) {
                return "888";
            }
            if (n % 2) {
                return "8" + string((n - 3) / 2, '9') + "8" + string((n - 3) / 2, '9') + "8";
            } else {
                return "8" + string((n - 4) / 2, '9') + "77" + string((n - 4) / 2, '9') + "8";
            }
        }
        if (k == 8) {
            if (n == 1) {
                return "8";
            }
            if (n == 2) {
                return "88";
            }
            if (n == 3) {
                return "888";
            }
            if (n == 4) {
                return "8888";
            }
            if (n == 5) {
                return "88888";
            }
            return "888" + string(n - 6, '9') + "888";
        }
        if (k == 7) {
            if (n == 1) {
                return "7";
            }
            if (n == 2) {
                return "77";
            }
            if (n == 3) {
                return "959";
            }
            if (n == 4) {
                return "9779";
            }
            int len = (n - 2) / 2;
            int r = 2;
            for (int i = 0; i < len - 1; i++) {
                r = (r * 10 + 9) % 7;
            }
            int r2 = 10 % 7;
            int len2 = n - len;
            for (int i = 0; i < len2 - 1; i++) {
                r2 = (r2 * 10) % 7;
            }
            r = (r * r2 + r) % 7;


            int r3 = 10 % 7;
            for (int i = 0; i < len - 1; i++) {
                r3 = (r3 * 10) % 7;
            }


            r = 7 - r;
            if (n % 2 == 0) {
                string res = "77";
                string a[8] = {"77", "99", "44", "66", "88", "33", "55", "77"};

                for (int i = 0; i <= 7; i++) {
                    if (r3 * i % 7 == r) {
                        res = a[i];
                        break;
                    }
                }

                // if (r == 7) {
                //     res = "77";
                // } else if (r == 6) {
