                // } else if (r == 5) {
                //     res = "33";
                // } else if (r == 4) {
                //     res =  "88";
                // } else if (r == 3) {
                //     res = "66";
                // } else if (r == 2) {
                //     res = "44";
                // } else if (r == 1) {
                //     res = "99";
                // }
                return string(len, '9') + res + string(len, '9');
            } else {
                string res = "959";
                string a[8] = {"959", "939", "989", "969", "949", "999", "979", "959"};

                for (int i = 0; i <= 7; i++) {
                    if (r3 * i % 7 == r) {
                        res = a[i];
