class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans(n, '9');
        if(k == 2) {
            ans[0] = '8';
            ans[n-1] = '8';
        } else if(k == 4) {
            ans[0] = '8';
            ans[n-1] = '8';
            if(n >= 2) {
                ans[1] = '8';
                ans[n-2] = '8';
            }
        } else if(k == 5) {
            ans[0] = '5';
            ans[n-1] = '5';
        } else if(k == 6) {
            if(n == 1) {
                ans[0] = '6';
            }else if(n == 2) {
                ans[0] = '6', ans[n-1] = '6';
            }else {
                // 如果是奇数，将中间位变8；如果是偶数，中间两位变7
                ans[0] = '8';
                ans[n-1] = '8';
                int t = n / 2;
                if(n % 2) {
                    ans[t] = '8';
                }else {
                    ans[t] = '7';
                    ans[t-1] = '7';
                }
            }
        } else if(k == 7) {
            // 7需要特殊处理
            ans = deal7(n);
        } else if(k == 8) {
            // 替换为888
            ans[0] = '8';
            ans[n-1] = '8';
            if(n >= 2) {
                ans[1] = '8';
                ans[n-2] = '8';
            }
            if(n >= 3) {
                ans[2] = '8';
                ans[n-3] = '8';
            }
        }
        // 1,3,9不用处理
        return ans;
    }

    string deal7(int n) {
        //逐位求和法：一个自然数从个位向左，逐位依次用1、3、2、－1、－3、－2、1、3、2、－1、－3、－2、……
        //分别乘各个数位上的数再相加，如果得数能被7整除，这个自然数就能被7整除。
        string ans(n, '9');
        long long sum = 0;
        vector<int> v = {1,3,2,-1,-3,-2};
        for(int i=0; i<n; i++) {
            sum += 9 * v[i%6];
        }
        if(sum % 7 == 0)    return ans;
        // 尝试替换
        if(n % 2) {
            int t = n/2;
            for(int i=8; i>=0; i--) {
                sum -= v[t%6];
                char c = '0'+i;
                ans[t] = c;
                if(sum % 7 == 0)    break;
            }
        } else {
            int t = n/2;
            for(int i=8; i>=0; i--) {
                sum -= v[t%6] + v[(t-1)%6];
                char c = '0'+i;
                ans[t] = c, ans[t-1] = c;
                if(sum % 7 == 0)    break;
            }
        }
        return ans;
    }
};
