class Solution {
public:
    bool check(const string &num_str) {
        int remainder = 0;

        for (char digit : num_str) {
            remainder = (remainder * 10 + (digit - '0')) % 7;
        }

        return remainder == 0;
    }

    string largestPalindrome(int n, int k) {
        if(k == 1 || k == 3 || k == 9){
            string s = string(n,'9');
            return s;
        }
        else if(k == 2){
            string s = string(n,'9');
            s[0] = s[n-1] = '8';
            return s;
        }
        else if(k == 5){
            string s = string(n,'9');
            s[0] = s[n-1] = '5';
            return s;
        }
        else if(k == 4){
            if(n<2){
                return "8";
            }
            else{
                string s = string(n,'9');
                s[0] = s[n-1] = s[1] = s[n-2] = '8';
                return s;
            }
        }
        else if(k == 8){
            if(n<3){
                return string(n,'8');
            }
            else{
                string s = string(n,'9');
                s[0] = s[n-1] = s[1] = s[n-2] = s[2] = s[n-3] = '8';
                return s;
            }
        }
        else if(k == 7){
            if(n<3){
                return string(n,'7');
            }
            if(n%2){
                string s = string(n,'9');
                for(int i=0;i<=9;i++){
                    s[n/2] = '9'-i;
                    if(check(s)){
                        return s;
                    }
                }
            }
            else{
                string s = string(n,'9');
                for(int i=0;i<=9;i++){
                    s[n/2 - 1] = '9' - i;
                    for(int j=0;j<=9;j++){
                        s[n/2] = '9' -j;
                        if(check(s) && i == j){
                            return s;
                        }
                    }
                }
            }
        }
        else{
            if(n == 1 || n == 2){
                return string(n,'6');
            }
            if(n%2){
                string s = string(n,'9');
                s[0] = s[n-1] = s[n/2] = '8';
                return s;
            }
            else{
                string s = string(n,'9');
                s[0] = s[n-1] = '8';
                s[n/2 - 1] = s[n/2] = '7';
                return s;
            }
        }
        return "";
    }
};
