class Solution {
public:


    bool pos(string &s)
    {
        int rem=0;
        int n=s.length();
        for(int i=0;i<n;i++)
        {
            rem=rem*10;
            rem+=s[i]-'0';
            rem=rem%7;
        }
        if(rem==0)
            return true;
        return false;
    }
    string largestPalindrome(int n, int k) {
        string ans;
        if(k==1)
        {
            for(int i=0;i<n;i++)
                ans.push_back('9');
        }
        else if(k==2)
        {
            ans.push_back('8');
            for(int i=1;i<n-1;i++)
                ans.push_back('9');
            if(n>1)
            ans.push_back('8');
        }
        else if(k==3 || k==9)
        {
            for(int i=0;i<n;i++)
                ans.push_back('9');
        }
        else if(k==4)
        {
            if(n<=4)
            {
                for(int i=0;i<n;i++)
                    ans.push_back('8');
            }
            else
            {
                ans="88";
                for(int i=2;i<n-2;i++)
                    ans.push_back('9');
                ans=ans+"88";

            }

        }
        else if(k==8)
        {
            if(n<=6)
            {
                for(int i=0;i<n;i++)
                    ans.push_back('8');
            }
            else
            {
                ans="888";
                for(int i=3;i<n-3;i++)
                    ans.push_back('9');
                ans=ans+"888";

            }
        }
        else if(k==5)
        {
            ans.push_back('5');
            for(int i=1;i<n-1;i++)
                ans.push_back('9');
            if(n>1)
            ans.push_back('5');
        }
        else if(k==6)
        {
            if(n==1)
                ans="6";
            else if(n==2)
                ans="66";
            else
            {
                if(n%2==0)
                {
                    ans.push_back('8');
                    for(int i=1;i<n/2-1;i++)
                        ans.push_back('9');
                    ans.push_back('7');
                    ans.push_back('7');
                    for(int i=1;i<n/2-1;i++)
                        ans.push_back('9');
                    ans.push_back('8');

                }
                else
                {
                    ans.push_back('8');
                    for(int i=1;i<n/2;i++)
                        ans.push_back('9');
                    ans.push_back('8');
                    for(int i=1;i<n/2;i++)
                        ans.push_back('9');
                     ans.push_back('8');

                }
            }
        }
        else
        {
            for(int i=0;i<n;i++)
                ans.push_back('9');
            if(n%2==1)
            {
                for(int j=9;j>=0;j--)
                {
                    ans[n/2]=j+'0';
                if(pos(ans))
                    return ans;
                }
            }
            else
            {
                for(int j=9;j>=0;j--)
                {
                    ans[n/2-1]='0'+j;
                    ans[n/2]='0'+j;
                    if(pos(ans))
                        return ans;
                }
            }
        }
        return ans;

    }
};
