int isdivisible7(string num)
{
    int n = num.length(), gSum=0;
    if (n == 0)
        return 1;
    if (n % 3 == 1) {
        num="00" + num;
        n += 2;
    }
    else if (n % 3 == 2) {
        num= "0" + num;
        n++;
    }
    int i, GSum = 0, p = 1;
    for (i = n - 1; i >= 0; i--) {
        int group = 0;
        group += num[i--] - '0';
        group += (num[i--] - '0') * 10;
        group += (num[i] - '0') * 100;
        gSum = gSum + group * p;
        p *= (-1);
    }

    return (gSum % 7 == 0);
}

class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) {
            return string(n, '9');
        } else if (k == 2) {
            if (n == 1) {
                return "8";
            } else {
                return "8" + string(n - 2, '9') + "8";
            } 
        } else if(k == 4) {
            if (n <= 4) {
                return string(n, '8');
            } else {
                return "88" + string(n - 4, '9') + "88";
            }
        } else if (k == 5) {
            if (n <= 2) {
                return string(n, '5');
            } else {
                return "5" + string(n - 2, '9') + "5";
            }
        } else if (k == 8) {
            if (n <= 6) {
                return string(n, '8');
            } else {
                return "888" + string(n - 6, '9') + "888";
            }
        } else if (k == 6) {
            if (n <= 2) {
                return string(n, '6');
            } else if (n % 2) {
                int half = n / 2;
                string t = "8" + (half - 1 > 0 ? string(half - 1, '9') : "");
                string t_rev = t;
                reverse(t_rev.begin(), t_rev.end());
                return t + "8" + t_rev;
            } else {
                int half = n / 2 - 1;
                string t = "8" + (half - 1 > 0 ? string(half - 1, '9') : "");
                string t_rev = t;
                reverse(t_rev.begin(), t_rev.end());
                return t + "77" + t_rev;
            }
        } else {
            if (n <= 2) {
                return string(n, '7');
            } 
            for (int dig = 9; dig >= 1; dig--) {
                if (n & 1) {
                    string t = string(n / 2, '9') + to_string(dig) + string(n / 2, '9');
                    if (isdivisible7(t)) {
                        return t;
                    }
                } else {
                    string t = string(n / 2 - 1, '9') + to_string(dig) + to_string(dig) + string(n / 2 - 1, '9');
                    if (isdivisible7(t)) {
                        return t;
                    }
                }
            }
        }
        return "";
    }
};
