class Solution {
public:
    bool div(const string& s) {
    int rem = 0;
    for(char c : s) {
        rem = (rem * 10 + (c - '0')) % 7;
    }
    return rem == 0;
    }

    string largestPalindrome(int n, int k) {
        string ans="";
        for(int i=0;i<n;i++){
            ans+="9";
        }
        if(k==1 || k==3 || k==9){
            return ans;
        }
        string ans2="";
        if(k==2){
           if(n==1)return "8";
           ans2+="8";
           for(int i=0;i<n-2;i++)ans2+="9";
           ans2+="8";
           return ans2; 
        }
        if(k==4){
            if(n==1)return "8";
            if(n==2)return "88";
            if(n==3)return "888";
            ans2+="88";
            for(int i=0;i<n-4;i++)ans2+="9";
            ans2+="88";
            return ans2;
        }
        if(k==8){
            if(n==1)return "8";
            if(n==2)return "88";
            if(n==3)return "888";
            if(n==4)return "8888";
            if(n==5)return "88888";
            ans2+="888";
            for(int i=0;i<n-6;i++)ans2+="9";
            ans2+="888";
            return ans2;
        }
        if(k==5){
            if(n==1)return "5";
            if(n==2)return "55";
            ans2+="5";
            for(int i=0;i<n-2;i++)ans2+="9";
            ans2+="5";
            return ans2;
        }
        if(k==6){
            if(n==1)return "6";
            if(n==2)return "66";
            ans2+="8";
            int x=(n-2)/2;
            if(n%2==0)x--;
            for(int i=0;i<x;i++)ans2+="9";
            if(n&1)ans2+="8";else ans2+="77";
            for(int i=0;i<x;i++)ans2+="9";
            ans2+="8";
            return ans2;
        }
        if(k==7){
            if(n==1)return "7";
            if(n==2)return "77";
            for(int i=0;i<n;i++)ans2+="9";
            if(n&1){
                int z=n/2;
                for(int i=9;i>=0;i--){
                   char c='0'+i;
                   ans2[z]=c;
                   if(div(ans2))return ans2;
                }
            }else{
                int z=n/2;
                for(int i=9;i>=0;i--){
                   char c='0'+i;
                   ans2[z]=c;
                   ans2[z-1]=c;
                   if(div(ans2))return ans2;
                }
            }
        }
        return "";
    }
};
