class Solution {
public:

    bool isDivisibleBySeven(string s) // source gfg
    {
        int remainder = 0;
        for (int i = 0; i < s.length(); i++) {
            int digit = remainder * 10 + (s[i] - '0');
            remainder = digit % 7;
        }

        return remainder == 0;
    }

    string largestPalindrome(int n, int k) {
        if(k == 1) {
            return string(n, '9');
        }
        else if(k == 2) {
            if(n == 1) {
                return "8";
            }
            if(n == 2) {
                return "88";
            }
            string s(n - 2, '9');
            return "8" + s + "8";
        }
        else if(k == 3) {
            return string(n, '9');
        }
        else if(k == 4) {
            if(n <= 4) {
                return string(n, '8');
            }
            return "88" + string(n - 4, '9') + "88";
        }
        else if(k == 5) {
            if(n <= 2) {
                return string(n, '5');
            }
            return "5" + string(n - 2, '9') + "5";
        }
        else if(k == 6) {
            if(n == 1) {
                return "6";
            }
            else if(n == 2) {
                return "66";
            }
            else if(n == 3) {
                return "888";
            }
            else if(n == 4) {
                return "8778";
            }
            if(n & 1) {
                return "8" + string((n - 3) / 2, '9') + "8" + string((n - 3) / 2, '9') + "8";
            }
            return "8" + string((n - 4) / 2, '9') + "77" + string((n - 4) / 2, '9') + "8";
        }
        else if(k == 7) {
            if(n <= 2) {
                return string(n, '7');
            }
            if(n & 1) {
                for(int i = 9; i >= 0; i--) {
                    if(isDivisibleBySeven(string((n - 1) / 2 , '9') + to_string(i) + string((n - 1) / 2 , '9'))) {
                        return string((n - 1) / 2 , '9') + to_string(i) + string((n - 1) / 2 , '9');
                    }
                }
            }
            for(int i = 9; i >= 0; i--) {
                if(isDivisibleBySeven(string((n - 2) / 2 , '9') + to_string(i) + to_string(i) + string((n - 2) / 2 , '9'))) {
                    return string((n - 2) / 2 , '9') + to_string(i) + to_string(i) + string((n - 2) / 2 , '9');
                }
            }
        }
        else if(k == 8) {
            if(n <= 6) {
                return string(n, '8');
            }
            return "888" + string(n - 6, '9') + "888";
        }
        return string(n, '9');
    }
};
