class Solution {
public:
    long long binpow(long long a, long long b, long long m) {
        a %= m;
        long long res = 1;
        while (b > 0) {
            if (b & 1)
                res = res * a % m;
            a = a * a % m;
            b >>= 1;
        }
        return res;
    }

    string largestPalindrome(int n, int k) {
        string ans;
        for (int i = 0; i < n; i++) ans.push_back('0');
        if (k == 1 || k == 3 || k == 9) {
            for (int i = 0; i < n; i++) ans[i] = '9';
        } else if (k == 2) {
            if (n <= 2) for (int i = 0; i < n; i++) ans[i] = '8';
            else {
                ans[0] = ans[n-1] = '8';
                for (int i = 1; i < n-1; i++) ans[i] = '9';
            }
        } else if (k == 4) {
            if (n <= 4) for (int i = 0; i < n; i++) ans[i] = '8';
            else {
                ans[0] = ans[1] = ans[n-1] = ans[n-2] = '8';
                for (int i = 2; i < n-2; i++) ans[i] = '9';
            }
        } else if (k == 5) {
            if (n <= 2) for (int i = 0; i < n; i++) ans[i] = '5';
            ans[0] = ans[n-1] = '5';
            for (int i = 1; i < n-1; i++) ans[i] = '9';
        } else if (k == 6) {
            if (n == 1) ans = "6";
            else if (n == 2) ans = "66";
            else if (n == 3) ans = "888";
            else {
                ans[0] = ans[n-1] = '8';
                for (int i = 1; i < n-1; i++) ans[i] = '9';
                if (n % 2 == 0) ans[n/2-1] = ans[n/2] = '7';
                else ans[n/2] = '8';
            }
        } else if (k == 8) {
            if (n <= 6) for (int i = 0; i < n; i++) ans[i] = '8';
            else {ans[0] = ans[1] = ans[2] = ans[n-1] = ans[n-2] = ans[n-3] = '8';
            for (int i = 3; i < n-3; i++) ans[i] = '9';}
        } else {
            for (int i = 0; i < n; i++) {
                ans[i] = '9';
            }
            int r = 0;
            if (n % 2 == 0) {
                // n/
                for (int i = 0; i < n; i++) {
                    if (i == n/2-1 || i == n/2) {
                        r = (10 * r) % 7;
                    } else {
                        r = (10 * r + 9) % 7;
                    }
                }
                for (int j = 9; j >= 0; j--) {
                    if ((r + j * binpow(10, n/2, 7) + j * binpow(10, n/2-1, 7)) % 7 == 0) {
                        ans[n/2] = ans[n/2-1] = char(j + '0');
                        break;
                    }
                }
            } else {
                for (int i = 0; i < n; i++) {
                    if (i == n/2) {
                        r = (10 * r) % 7;
                    } else {
                        r = (10 * r + 9) % 7;
                    }
                }
                for (int j = 9; j >= 0; j--) {
                    if ((r + j * binpow(10, n/2, 7)) % 7 == 0) {
                        ans[n/2] = char(j + '0');
                        break;
                    }
                }
            }
        }
        return ans;
    }
};
