class Solution {
public:
    bool isPalindrome(const string& s) {
    return s == string(s.rbegin(), s.rend());
}

void subtractOne(string& s) {
    int i = s.length() - 1;
    while (i >= 0 && s[i] == '0') {
        s[i] = '9';
        i--;
    }
    if (i >= 0) {
        s[i]--;
    }
    if (s[0] == '0' && s.length() > 1) {
        s = s.substr(1);
    }
}

bool isDivisibleBy7(const string& s) {
    int rem = 0;
    for (char c : s) {
        rem = (rem * 10 + (c - '0')) % 7;
    }
    return rem == 0;
}

string largestPalindromeDivisibleBy7(int n) {
    string left;
    if (n % 2 == 0) {
        left = string(n / 2, '9');
    } else {
        left = string(n / 2 + 1, '9');
    }

    while (true) {
        string num;
        if (n % 2 == 0) {
            num = left + string(left.rbegin(), left.rend());
        } else {
            num = left + string(left.rbegin() + 1, left.rend());
        }

        if (isDivisibleBy7(num)) {
            return num;
        }

        subtractOne(left);
        if (left.empty()) {
            return "";
        }
    }
}

