class Solution {

    bool isdivisible7(string num)
    {
        int n = num.length(), gSum=0;
        if (n == 0)
            return 1;

        // Append required 0s at the beginning.
        if (n % 3 == 1) {
            num="00" + num;
            n += 2;
        }
        else if (n % 3 == 2) {
            num= "0" + num;
            n++;
        }

        // add digits in group of three in gSum
        int i, GSum = 0, p = 1;
        for (i = n - 1; i >= 0; i--) {

            // group saves 3-digit group
            int group = 0;
            group += num[i--] - '0';
            group += (num[i--] - '0') * 10;
            group += (num[i] - '0') * 100;

            gSum = gSum + group * p;

            // generate alternate series of plus
            // and minus
            p *= (-1);
        }

        return (gSum % 7 == 0);
    }

    string get7(int n) {
        string ans(n, '9');

        if(n & 1) {
            for(char c = '9'; c >= '0'; c--) {
                ans[n/2] = c;
                if(isdivisible7(ans))
                    return ans;
            }
        } else {
            for(char c = '9'; c >= '0'; c--) {
                ans[n/2 - 1] = ans[n/2] = c;
                if(isdivisible7(ans))
                    return ans;
            }
        }
        return "";
    }

public:
    string largestPalindrome(int n, int k) {
        string ans;
        ans.resize(n);
        if(k == 1 || k == 3 || k == 9) {
            ans.assign(n, '9');
        } else if(k == 2) {
            ans[0] = '8';
            ans[n-1] = '8';

            for(int i=1; i<=n-2; i++) {
                ans[i] = '9';
            }
        } else if(k == 4) {
            for(int i=0; i<min(n, 2); i++) {
                ans[i] = '8';
                ans[n - i - 1] = '8';
            }
            for(int i=2; i<=n-3; i++) ans[i] = '9';
        } else if(k == 5) {
            ans[0] = '5';
            ans[n-1] = '5';
            for(int i=1; i<=n-2; i++) {
                ans[i] = '9';
            }
        } else if(k == 6) {
            if(n == 1) ans = "6";
            else if(n == 2) ans = "66";
            else {
                // first last and mid is 8, rest 9
                if(n & 1) {
                    ans.assign(n, '9');
                    ans[0] = ans[n-1] = ans[n/2] = '8';
                } else {
                    ans.assign(n, '9');
                    ans[0] = ans[n-1] = '8';
                    ans[n/2 - 1] = ans[n/2] = '7';
                }
            }
        } else if(k == 7) {
            if(n == 1) ans = "7";
            else if(n == 2) ans = "77";
            else if(n == 3) ans = "959";
            else if(n == 4) ans = "9779";
            else if(n == 5) ans = "99799";
            else ans = get7(n);
        } else if(k == 8) {
            for(int i=0; i<min(n, 3); i++) {
                ans[i] = '8';
                ans[n - i - 1] = '8';
            }
            for(int i=3; i<=n-4; i++) ans[i] = '9';
        }
        return ans;
    }
};
