class Solution {
public:
    string charvec(vector<char>&v) {
        string res = "";
        for (auto i : v) res += i;
        return res;
    }
    string intvec(vector<int>&v) {
        string res = "";
        for (auto i : v) res += to_string(i);
        return res;
    }
    bool div7(vector<int>&v) {
        int n = v.size();
        int rem = 0;
        for (int i = 0; i < n; i++) {
            int x = rem * 10 + v[i];
            rem = x%7;
        }
        return rem == 0;
    }
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) {
            string s = "";
            for (int i = 0; i < n; i++) s += "9";
            return s;
        }
        if (k == 2) {
            vector<char>res(n); 
            res[n-1] = '8';
            res[0] = '8';
            for (int i = 1; i < n-1; i++) res[i] = '9';
            return charvec(res);
        }
        if (k == 4) {
            vector<char>res(n); 
            res[n-1] = '8';
            res[0] = '8';
            if (n > 2) {res[n-2] = '8'; res[1] = '8';}
            for (int i = 2; i < n-2; i++) res[i] = '9';
            return charvec(res);
        }
        if (k == 8) {
            vector<char>res(n); 
            res[n-1] = '8';
            res[0] = '8';
            if (n > 2) {res[n-2] = '8'; res[1] = '8';}
            if (n > 4) {res[n-3] = '8'; res[2] = '8';}
            for (int i = 3; i < n-3; i++) res[i] = '9';
            return charvec(res);
        }
        if (k == 5) {
            vector<char>res(n); 
            res[n-1] = '5';
            res[0] = '5';
            for (int i = 1; i < n-1; i++) res[i] = '9';
            return charvec(res);
        }
        if (k == 6) {
            if (n == 1) return "6";
            if (n == 2) return "66";
            vector<int>res(n);
            res[n-1] = 8;
            res[0] = 8;
            int total = 16;
            for (int i = 1; i < (n+1)/2 - 1; i++) {
                total += 18;
                res[i] = 9;
                res[n - i - 1] = 9;
            }
            if (n%2) {
                for (int i = 9; i >= 0; i--) {
                    if ((total + i)%3 == 0) {res[n/2] = i; break;}
                }
            }
            else {
                for (int i = 9; i >= 0; i--) {
                    if ((total + i + i)%3 == 0) {res[n/2] = i; res[n/2 - 1] = i; break;}
                }
            }
            return intvec(res);
        }
        if (k == 7) {
            if (n == 1) return "7";
            if (n == 2) return "77";
            vector<int>res(n);
            for (int i = 0; i < (n+1)/2 - 1; i++) {res[i] = 9; res[n - i - 1] = 9;}
            for (int j = 9; j >= 0; j--) {
                if (n%2) res[n/2] = j; 
                else {res[n/2] = j; res[n/2 - 1] = j;}
                if (div7(res)) break;
            }
            return intvec(res);
        }
        return "";
    }
};
