class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return "".join(["9" for _ in range(n)])
        elif k == 2:
            tmp = "8" if n > 1 else ""
            if n == 1:
                return "8"
            return tmp + "".join(["9" for _ in range(n - 2)]) + tmp
        elif k == 4:
            tmp = "88" if n >= 4 else ""
            if n < 4:
                return "".join("8" for _ in range(n))
            return tmp + "".join(["9" for _ in range(n - 4)]) + tmp
        elif k == 8:
            tmp = "888" if n >= 6 else ""
            if n < 6:
                return "".join("8" for _ in range(n))
            return tmp + "".join(["9" for _ in range(n - 6)]) + tmp
        elif k == 5:
            tmp = "5" if n > 1 else ""
            if n == 1:
                return "5"
            return tmp + "".join(["9" for _ in range(n - 2)]) + tmp
        elif k == 6:
            # 8 ... 8 where 8 + 8 + 。。。 can be divided by 3
            if n == 1:
                return "6"
            if n == 2:
                return "66"

            if n % 2 == 0:
                return "8" + "".join("9" for _ in range(n // 2 - 2)) + "77" + "".join("9" for _ in range(n // 2 - 2)) + "8"
            else:
                return "8" + "".join("9" for _ in range(n // 2 - 1)) + "8" + "".join("9" for _ in range(n // 2 - 1)) + "8"
        else:
            t = n // 24
            a = n % 24

            if a == 0:
                return "".join("999999999999" for _ in range(t * 2))
            for i in range(9,-1,-1):
                tmp = "".join("9" for _ in range(a // 2 - 1)) if a % 2 == 0 else "".join("9" for _ in range(a // 2))
                tmpSoln = tmp + str(i) + tmp if n % 2 == 1 else tmp + str(i) + str(i) + tmp
                if int(tmpSoln) % 7 == 0:
                    return "".join("999999999999" for _ in range(t)) + tmpSoln + "".join("999999999999" for _ in range(t))

            return ""
