class Solution {
public:
   #define ll long long int
const int mod = 1e9 + 7;

bool pal(int n)
{
    string s;
    while (n)
    {
        int x = n % 10;
        n /= 10;
        s.push_back(x + '0');
    }
    string t = s;
    reverse(t.begin(), t.end());
    return s == t;
}
bool chk(string s)
{
    // 9994999
    int an = 0, cr = 0;
    int n = s.size();
    for (int i = 0; i < n; i++)
    {
        int x = s[i] - '0';
        int t = cr * 10 + x;
        cr = t % 7;
    }
    if (cr == 0) return true;
    return false;
}
string largestPalindrome(int n, int k) {
    string an;
    int x;
    if (n <= 4) {
        for (int i = 1; i < 10000; i++) {
            string tm = to_string(i);
            if (tm.size() > n) break;
            if (pal(i)) {
                if (i % k == 0) {
                    an = to_string(i);
                }
            }
        }

        return an;
    }

    for (int i = 0; i < n; i++) an.push_back('9');

    if (k == 2) {
        an[0] = '8'; an[n - 1] = '8';
    }
    if (k == 4)
    {
        an[0] = an[1] = an[n - 1] = an[n - 2] = '8';
    }
    if (k == 5) {
        an[0] = an[n - 1] = '5';
    }
    if (k == 6)
    {
        an[0] = an[n - 1] = '8';
        if (n % 2 == 0) {
            an[n / 2] = an[n / 2 - 1] = '7';
        }
        else an[n / 2] = '8';
    }
    if (k == 8)
    {
        if(n < 6){
            for(int i = 0;i < n;i++) an[i] = '8';
        }
        else{
            an[0] = an[1] = an[2] = an[n-1] = an[n-2] = an[n-3] = '8';
        }
    }

    if (k == 7)
    {
        if (n % 2 == 1) {
            string tm = an;
            for (int i = 9; i >= 1; i--) {
                tm[n / 2] = (i + '0');
                if (chk(tm)) {
                    an = tm; break;
                }
            }
        }
        else {
            string tm = an;
            for (int i = 9; i >= 1; i--) {
                tm[n / 2] = tm[n / 2 - 1] = (i + '0');
                if (chk(tm)) {
                    an = tm; break;
                }
            }
        }
    }
   return an;
}
};
