class Solution {
  public:
    string constructFour(int n)
    {
        if (n < 5) {
            return string(n, '8');
        }
        return "88" + string(n - 4, '9') + "88";
    }
    string constructEight(int n)
    {
        if (n < 7) {
            return string(n, '8');
        }
        return "888" + string(n - 6, '9') + "888";
    }
    string constructSeven(int n)
    {
        const string magic_table[] { "", "7", "77", "5",
            "77", "7", "", "4", "44", "6", "44", "4" };
        const int cycle_len
            = sizeof(magic_table) / sizeof(string);
        int idx = n % cycle_len;
        int remain_len = n - (int)magic_table[idx].length();
        assert(remain_len % 2 == 0);
        return string(remain_len / 2, '9')
            + magic_table[idx]
            + string(remain_len / 2, '9');
    }
    string constructSix(int n)
    {
        if (n < 3) {
            return string(n, '6');
        }
        if (n % 2 == 0) {
            return '8' + string(n / 2 - 2, '9') + "77"
                + string(n / 2 - 2, '9') + '8';
        }
        return '8' + string(n / 2 - 1, '9') + "8"
            + string(n / 2 - 1, '9') + '8';
    }
    string largestPalindrome(int n, int k)
    {
        assert(n > 0);
        assert(1 <= k && k <= 9);
        const char max_under_ten[10] = { '0', '9', '8', '9',
            '8', '5', '6', '7', '8', '9' };
        if (n == 1) {
            return string(1, max_under_ten[k]);
        }
        if (k == 1 || k == 2 || k == 3 || k == 5
            || k == 9) {
            return max_under_ten[k] + string(n - 2, '9')
                + max_under_ten[k];
        }
        if (k == 8) {
            return constructEight(n);
        }
        if (k == 4) {
            return constructFour(n);
        }
        if (k == 7) {
            return constructSeven(n);
        }
        return constructSix(n);
    }
};
