int d[6] = {2, 6, 4, 5, 1, 3};
int f[6] = {1, 3, 2, 6, 4, 5};
class Solution {
public:
    string largestPalindrome(int n, int k) {
        stringstream ss;

        // int tt = 9;
        // for(int i = 0; i < 8; i ++ ){
        //     cout << tt % 7 << endl;
        //     tt *= 10;

        // }


        for(int i = 0; i < n; i ++ ) ss << "9";
        string s = ss.str();
        if(k == 1 || k == 3 || k == 9){
            return s;    
        }
        if(k == 2){
            s[0] = s[n - 1] = '8';
            return s;
        }
        if(k == 5){
            s[0] = s[n - 1] = '5';
            return s;
        }
        if(k == 4){
            s[0] = s[n - 1] = '8';
            if(n > 1)s[1] = s[n - 2] = '8';
            return s;
        }
        if(k == 8){
            s[0] = s[n - 1] = '8';
            if(n > 1)s[1] = s[n - 2] = '8';
            if(n > 2)s[2] = s[n - 3] = '8';
            return s;
        }
        if(k == 6){
            if(n == 1) return "6";
            s[0] = s[n - 1] = '8';
            if(n & 1) s[n / 2] = '8';
            else s[n / 2] = s[n / 2 - 1] = char(s[n / 2] - 2);
            return s;
        }
        int t = 0;
        for(int i = 0; i < n; i ++ ){
            t = t + d[i % 6];
            t = t % 7;
        }
        // cout <<"t= " << t << endl;
        if(n & 1){
            int x = f[n / 2 % 6];
            for(int i = 9; i >= 0; i -- ){
                if((((9 - i) * x) % 7) == t){
                    s[n / 2] = char('0' + i);
                    return s;
                }
            }
        } else {
            int x = f[n / 2 % 6];
            int y = f[(n / 2 - 1) % 6];
            for(int i = 9; i >= 0; i -- ){
                if((x * (9 - i) + y * (9 - i)) % 7 == t){
                    s[n / 2] = s[n / 2 - 1] =  char('0' + i);
                    return s;
                }
            }
        }
        return s;
    }
};
