class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans;
        if (k == 1 || k == 3 || k == 9) {
            for (int i = 0; i < n; i++) {
                ans += '9';
            }
        } else if (k == 2) {
            if (n == 1) {
                ans = "8";
            } else {
                ans += '8';
                for (int i = 0; i < n - 2; i++) {
                    ans += '9';
                }
                ans += '8';
            }
        } else if (k == 4) {
            if (n <= 4) {
                for (int i = 0; i < n; i++) {
                    ans += '8';
                }
            } else {
                ans += "88";
                for (int i = 0; i < n - 4; i++) {
                    ans += '9';
                }
                ans += "88";
            }
        } else if (k == 5) {
            if (n == 1) {
                ans = "5";
            } else {
                ans += '5';
                for (int i = 0; i < n - 2; i++) {
                    ans += '9';
                }
                ans += '5';
            }
        } else if (k == 6) {
            if (n == 1) {
                ans = "6";
            } else if (n == 2) {
                ans = "66";
            } else {
                ans += '8';
                if (n % 2 == 1) {
                    for (int i = 0; i < (n - 3) / 2; i++) {
                        ans += '9';
                    }
                    ans += '8';
                    for (int i = 0; i < (n - 3) / 2; i++) {
                        ans += '9';
                    }
                    ans += '8';
                } else {
                    for (int i = 0; i < (n - 4) / 2; i++) {
                        ans += '9';
                    }
                    ans += "77";
                    for (int i = 0; i < (n - 4) / 2; i++) {
                        ans += '9';
                    }
                    ans += '8';
                }

            }
        } else if (k == 7) {
            vector<int> v(n, 0);
            generate(n, v);
            for (int i = n - 1; i >= 0; i--) {
                ans += (v[i] + '0');
            }
        } else if (k == 8) {
            if (n <= 6) {
                for (int i = 0; i < n; i++) {
                    ans += '8';
                }
            } else {
                for (int i = 0; i < 3; i++) {
                    ans += '8';
                }
                for (int i = 0; i < n - 6; i++) {
                    ans += '9';
                }
                for (int i = 0; i < 3; i++) {
                    ans += '8';
                }
            }
        }
        return ans;
    }

    void generate(int n, vector<int> &v) {
        for (int i = 0; i <= (n - 1) / 2; i++) {
            v[i] = 9;
            v[n - i - 1] = 9;
        }
        int p = (n - 1) / 2;
        while (!func(n, v)) {
            v[p] -= 1;
            if (p != n - p - 1) {
                v[n - p - 1] -= 1;
            }

            while (v[p] < 0) {
                v[p] += 10;
                v[p - 1] -= 1;
                if (p != n - p - 1) {
                    v[n - p - 1] += 10;
                    v[n - p - 1 + 1] -= 1;
                }
                p -= 1;
            }
        }
    }

    bool func(int n, vector<int> &v) {
        vector<int> vv = v;
        int p = 0;
        while (n - p > 8) {
            int last = vv[p];
            p += 1;
            vv[p] -= 2 * last;
            int tmpp = p;
            while (vv[tmpp] < 0) {
                vv[tmpp] += 10;
                vv[tmpp + 1] -= 1;
                if (vv[tmpp] > 0) {
                    tmpp += 1;
                }
            }
        }
        long long tmp = 0;
        for (int i = n - 1; i >= p; i--) {
            tmp *= 10;
            tmp += vv[i];
        }
        if (tmp % 7 == 0) {
            return true;
        }
        return false;
    }
};
