class Solution {
public:
    string largestPalindrome(int n, int k) {
        string res(n, '9');
        if(k == 5) {
            res[0] = res.back() = '5';
        }
        else if(k == 2) {
            res[0] = res.back() = '8';
        }
        else if(k == 4 || k == 8) {
            string res1(n, '8');
            if(k == 4) {
                for(int i = 2; i < n - 2; i++) {
                    res1[i] = '9';
                }
            }
            else{
                for(int i = 3; i < n - 3; i++) {
                    res1[i] = '9';
                }
            }
            return res1;
        }
        else if(k == 6) {
            string res1(n, '8');
            if(n == 1) {
                res1 = "6";
            }
            else if(n == 2) {
                res1 = "66";
            }
            else if(n == 3) {
                return res1;
            }
            else {
                long long sum = 8 * n;
                for(int i = 1; i < n - 1; i++) {
                    res1[i] = '9';
                    sum++;
                }
                int rem = sum % 3;
                if(rem == 0) return res1;
                else if(rem == 1) {
                    if(n % 2) {
                        int center = n / 2;
                        res1[center] = '8';
                    }
                    else {
                        int left = n / 2;
                        int right = left - 1;
                        res1[left] = res1[right] = '7';
                    }
                }
                else {
                    if(n % 2) {
                        int center = n / 2;
                        res1[center] = '7';
                    }
                    else {
                        int left = n / 2;
                        int right = left - 1;
                        res1[left] = res1[right] = '8';
                    }
                }
                return res1;
            }
            return res1;
        }
        else if(k == 7) {
            if(n == 1) {
                res = "7";
            }
            else if(n == 2) {
                res = "77";
            }
            else {
                string t1 = "574647";
                string t2 = "779449";
                if(n % 2 == 1) {
                    res[n / 2] = t1[((n - 3) / 2) % 6];
                }
                else {
                    res[n / 2] = res[n / 2 - 1] = t2[((n - 2) / 2) % 6];
                }
            }
            return res;
        }
        return res;
    }
