class Solution {
public:
 string largestP(int n, int k) {
    if (k == 7) {
        string half = "";
        int half_len = (n + 1) / 2;
        for (int i = 0; i < half_len; i++) {
            half += '9';
        }

        while (true) {
            string full_palindrome = half + string(half.rbegin() + (n % 2), half.rend());
            if (isDivisibleBy7(full_palindrome)) {
                return full_palindrome;
            }
            decrementHalf(half);
        }
    }

    return "";
}

bool isDivisibleBy7(const string &num_str) {
    int remainder = 0;
    for (char c : num_str) {
        remainder = (remainder * 10 + (c - '0')) % 7;
    }
    return remainder == 0;
}

void decrementHalf(string &half) {
    int len = half.size();
    for (int i = len - 1; i >= 0; i--) {
        if (half[i] > '0') {
            half[i]--;
            break;
        } else {
            half[i] = '9';
        }
    }
}


