public:
    string largestPalindrome(int n, int k) {
        if(k == 7){
            for(int i = 0;i<n;i++)ans+='9';
            for(char i = 9;i>=0;i--){
                if(n&1)ans[n/2] = i+'0';
                else ans[n/2] = ans[n/2-1] = i+'0';
                int res = 0;
                for(int j = 0;j<ans.size();j++){
                    res = (res*10+(ans[j]-'0'))%k;
                }
                // if(i == 7){
                //     cout<<ans<<endl;
                // }
                if(res == 0){
                    break;
                }
            }
        }
        if(k == 1 || k == 3 || k == 9){
            for(int i = 0;i<n;i++){
                ans+='9';
            }
        }
        if(k == 2){
            ans+='8';
            if(n>=2){
                for(int i = 2;i<n;i++){
                    ans+='9';
                }
                ans+='8';
            }
        }
        if(k == 4){
            for(int i = 1;i<=n;i++){
                if(i<=2 || i>=n-1){
                    ans+='8';
                }else{
                    ans+='9';
                }
            }
        }
        if(k == 6){
            if(n&1){
                if(n == 1)ans+='6';
                else{
                    for(int i = 1;i<=n;i++){
                        if(i == 1 || i == n || i == (n+1)/2){
                            ans+='8';
                        }else{
                            ans+='9';
                        }
                    }
                }
            }else{
                if(n == 2)ans+="66";
                else{
                    ans+='8';
                    for(int i = 2;i<n;i++)ans+='9';
                    ans[n/2] = '7';
                    ans[n/2-1] = '7';
                    ans+='8';
                }
            }
        }
        if(k == 8){
            for(int i = 1;i<=n;i++){
                if(i<=3 || i>=n-2){
                    ans+='8';
                }else{
                    ans+='9';
                }
            }
        }
        if(k == 5){
            ans+='5';
            if(n>=2){
                for(int i = 2;i<n;i++){
                    ans+='9';
                }
                ans+='5';
            }
        }
        return ans;
        // len = n;
        // cs = k;
        // dfs(1,(n+1)/2,false);
        return ans;
    }
