class Solution {
public:
    string largestPalindrome(int n, int k) {
        string res;
        res.resize(n);
        const static int seven[]={1,3,2,6,4,5};
        switch(k){
            case 1:
            case 3:
            case 9:
                for(int i=0;i<n;i++)
                    res[i]='9';
                break;
            case 8:
            case 4:
            case 2:
                res[0]=res[n-1]='8';
                for(int i=(n-1)>>1;i;i--)
                    res[i]=res[n-1-i]='9';
                if(n>=5 && k==8)res[2]=res[n-3]='8';
                if(n>=3 && k!=2)res[1]=res[n-2]='8';
                break;
            case 5:
                res[0]=res[n-1]='5';
                for(int i=(n-1)>>1;i;i--)
                    res[i]=res[n-1-i]='9';
                break;
            case 6:
                if(n<=2)res[0]=res[n-1]='6';
                else{
                    res[0]=res[n-1]='8';
                    if(n&1)res[n>>1]='8';
                    else res[n>>1]=res[(n>>1)-1]='7';
                    for(int i=(n-1)>>1;--i;)
                        res[i]=res[n-1-i]='9';
                }
                break;
            case 7:
                int yushu=0;
                for(int i=(n-1)>>1;i;){
                    i--;
                    res[i]=res[n-1-i]='9';
                    yushu+=(seven[i%6]+seven[(n-i-1)%6])<<1;
                    yushu%=7;
                }
                if(yushu)yushu=7-yushu;
                int temp;
                if(n&1)temp=seven[(n>>1)%6];
                else temp=(seven[(n>>1)%6]+seven[((n>>1)-1)%6])%7;
                for(int i=9;i>2;i--)
                    if(((temp*i)%7)==yushu){
                        res[n>>1]=res[(n-1)>>1]=i+'0';
                        break;
                    }
                break;
        }
        return res;
    }
};
