class Solution:

    sys.set_int_max_str_digits(100000)
    def largestPalindrome(self, n: int, k: int) -> str:
        ret = ""
        if k == 1 or k == 3 or k == 9:
            for _ in range(n):
                ret += "9"
        elif k == 2:
            if n == 1:
                ret = "8"
            else:
                ret += "8"
                for _ in range(n-2):
                    ret += "9"
                ret += "8"
        elif k == 4:
            if n <= 4:
                for _ in range(n):
                    ret += "8"
            else:
                ret += "88"
                for _ in range(n-4):
                    ret += "9"
                ret += "88"
        elif k == 5:
            if n == 1:
                ret = "5"
            else:
                ret += "5"
                for _ in range(n-2):
                    ret += "9"
                ret += "5"
        elif k == 6:
            if n <= 2:
                for _ in range(n):
                    ret += "6"
            else:
                if n & 1 == 1:
                    ret += "8"
                    m = (n - 3) // 2
                    for _ in range(m):
                        ret += "9"
                    ret += "8"
                    for _ in range(m):
                        ret += "9"
                    ret += "8"
                else:
                    ret += "8"
                    m = (n - 4) // 2
                    for _ in range(m):
                        ret += "9"
                    ret += "77"
                    for _ in range(m):
                        ret += "9"
                    ret += "8"
        elif k == 7:
            if n <= 2:
                for _ in range(n):
                    ret += "7"
            else:
                for _ in range(n):
                    ret += "9"
                ret = int(ret)
                remainder = ret % 7
                if n & 1 == 1:
                    multiplier = 10 ** (n // 2)
                    for i in range(9):
                        if i * multiplier % 7 == remainder:
                            ret = str(ret - i * multiplier)
                            break
                else:
                    multiplier = 10 ** (n // 2 - 1)
                    for i in range(9):
                        if ((i * 10 + i) * multiplier) % 7 == remainder:
                            ret = str(ret - (i * 10 + i) * multiplier)
                            break
        elif k == 8:
            if n <= 6:
                for _ in range(n):
                    ret += "8"
            else:
                ret += "888"
                for _ in range(n-6):
                    ret += "9"
                ret += "888"
        return ret
