class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans(n, '9');
        if (k == 1 || k == 3 || k == 9) {
            return ans;
        }
        if (k == 2) {
            ans[0] = '8';
            if (n - 1 >= 0) ans[n - 1] = '8';
            return ans;
        }
        if (k == 4) {
            ans[0] = '8';
            if (n >= 1) ans[1] = '8';
            if (n - 1 >= 0) ans[n - 1] = '8';
            if (n - 2 >= 0) ans[n - 2] = '8';
            return ans;
        }
        if (k == 5) {
            ans[0] = '5';
            if (n - 1 >= 0) ans[n - 1] = '5';
            return ans;
        }
        if (k == 6) {
            if (n == 1) return "6";
            if (n == 2) return "66";
            if (n == 3) return "888";
            if (n == 4) return "8778";
            ans[0] = '8';
            ans[n - 1] = '8';
            if (n % 2 == 1) {
                ans[n / 2] = '8';
            }
            else if (n % 2 == 0) {
                ans[n / 2 - 1] = '7';
                ans[n / 2] = '7';
            }
            return ans;
        }
        if (k == 7) {
            if (n == 1) return "7";
            if (n == 2) return "77";
            for (int i = 9; i >= 0; --i) {
                if (n % 2 == 0) {
                    ans[(n + 1) / 2 - 1] = char(48 + i); 
                    ans[(n + 1) / 2] = char(48 + i);
                }
                else {
                    ans[(n + 1) / 2 - 1] = char(48 + i);
                }
                int rest = 0;
                for (int j = 0; j < n; ++j) {
                    rest = (rest * 10 + ans[j] - 48) % 7;
                }
                if (rest == 0) return ans;
            }
        }
        if (k == 8) {
            for (int i = 0; i < 3; ++i) {
                if (i < n) ans[i] = '8';
            }
            for (int i = 1; i <= 3; ++i) {
                if (n - i >= 0) ans[n - i] = '8';
            }
            return ans;
        }
        return ans;
    }
};
