    def divide(self, n: str, k: int):
        r = 0
        for c in n:
            r = (r * 10 + int(c)) % k
        return r == 0

    def des(self, i: str):
        c = int(i[-1])
        if c != 0:
            return i[:-1] + str(c-1)
        else:
            for idx in range(len(i)-1, -1, -1):
                if i[idx] != '0':
                    return i[:idx] + str(int(i[idx]) - 1) + "9" * (len(i) - idx)

    def largestPalindrome(self, n: int, k: int) -> str:
        # self.debug(k)
        if k == 1:
            return "9" * n
        elif k == 2:
            if n <= 2:
                return "8" * n
            else:
                return "8" + "9" * (n-2) + "8"
        elif k == 3:
            return "9" * n
        elif k == 4:
            if n <= 4:
                return "8" * n
            else:
                return "8" * 2 + "9" * (n-4) + "8" * 2
        elif k == 5:
            if n <= 2:
                return "5" * n
            else:
                return "5" + "9" * (n-2) + "5"
        elif k == 6:
            if n <= 2:
                return "6" * n
            elif n % 2:
                return "8" + "9" * ((n-3)//2) + "8" + "9" * ((n-3)//2) + "8"
            else:
                return "8" + "9" * ((n-4)//2) + "77" + "9" * ((n-4)//2) + "8"
        elif k == 7:
            if n <= 2:
                return "7" * n
            elif n == 3:
                return "959"
            elif n == 4:
                return "9779"
            elif n == 5:
                return "99799"
            elif n == 6:
