                return "9994999"
            elif n == 8:
                return "99944999"
            elif n == 9:
                return "999969999"
            elif n == 10:
                return "9999449999"
            elif n == 11:
                return "99999499999"
            else:
                i = "9" * (n // 2)
                while 1:
                    if n % 2:
                        for j in range(9, -1, -1):
                            t = i + str(j) + i[::-1]
                            if self.divide(t, k):
                                return t
                    else:
                        t = i + i[::-1]
                        if self.divide(t, k):
                            return t
                    i = self.des(i)
        elif k == 8:
