class Solution {
public:
    string largestPalindrome(int n, int k) {
        string res = string(n, '9');
        if(k == 2) {
            res[0] = res[n - 1] = '8';
        }
        if(k == 5) {
            res[0] = res[n - 1] = '5';
        }
        if(k == 4){
            if(n <= 4) res = string(n, '8');
            else {
                res[0] = res[1] = res[n - 1] = res[n - 2] = '8';
            }
        }
        if(k == 8){
            if(n <= 6) res = string(n, '8');
            else {
                res[0] = res[1] = res[2] = res[n - 1] = res[n - 2] = res[n - 3] = '8';
            }
        }

        if(k == 6) {
            if(n == 1) res = "6";
            if(n == 2) res = "66";
            if(n > 2) {
                res[0] = res[n - 1] = '8';
                if(n & 1) {
                    for(int i = 9; i >= 1; --i) {
                        int sum = i + 8 * 2 + 9 * (n - 3);
                        if(sum % 3 == 0) {
                            res[n / 2] = i + '0';
                            break;
                        }
                    }
                } else {
                    for(int i = 9; i >= 1; --i) {
                        int sum = i * 2 + 8 * 2 + 9 * (n - 4);
                        if(sum % 3 == 0) {
                            res[n / 2] = res[n /2 - 1] = i + '0';
                            break;
                        }
                    }
                }
            }
        }
        if(k == 7) {
            if(n == 1) res = "7";
            if(n == 2) res = "77";
            if(n > 2) {
                for(int i = 9; i >= 1; --i) {
                    if(n & 1) res[n / 2] = i + '0';
                    else res[n / 2] = res[n /2 - 1] = i + '0';
                    int sum = 0;
                    int sign = 1;
                    int j = n - 3;
                    while(j >= -3) {
                        int a = j >= 0 ? res[j] - '0' : 0;
                        int b = j >= -1 ? res[j + 1] - '0' : 0;
                        int c = j >= -2 ? res[j + 2] - '0' : 0;
                        sum += sign * (a * 100 + b * 10 + c);
                        sign *= -1;
                        j -= 3;
                    }
                    // cout << i << ' ' << sum << ' ';
                    if(sum % 7 == 0) break;
                }
            }
        }

        return res;
    }
};
