typedef long long ll;
//3 2 1 0
//2 1 0
int is[100005][10][10];
class Solution {
public:
    string largestPalindrome(int n, int k) {
        vector<ll> a(n);
        if (n == 1) {
            string s;
            for (int i = 10; i >= 0; i--) {
                if (i * k < 10) {
                    s.push_back(i * k + '0');
                    return s;
                }
            }
            return s;
        }
        for(int i = 0; i < n + 1; i++) {
            for(int j = 0; j < 10; j++) {
                for(int k = 0; k < 10; k++) is[i][j][k] = 0;
            }
        }
        a[0] = 1;
        vector<int> te(10);
        for (int i = 1; i < n; i++) a[i] = a[i - 1] * 10 % k;
        if (n & 1) {
            for (int i = 0; i < 10; i++) is[n / 2][i][(i * a[n / 2]) % k] = 1, te[(i * a[n / 2]) % k] = 1;
        }
        else {
            for (int i = 0; i < 10; i++) is[n / 2][i][(i * a[n / 2] + i * a[n - n / 2 - 1]) % k] = 1, te[(i * a[n / 2] + i * a[n - n / 2 - 1]) % k] = 1;
        }


        for (int i = n / 2 + 1; i < n; i++) {
            int t = 1;
            vector<int> te1 = te;
            for (int j = 0; j < 10; j++) {
                ll num = (a[i] * j + a[n - i - 1] * j) % k;
                for (int h = 0; h < 10; h++) {
                    if (!te[h]) continue;
                    is[i][j][(h + num) % k] = 1;
                    te1[(h + num) % k] = 1;
                }
            }
            te = te1;
        }
        string ans;
        ans.assign(n, '0');
        ll t = 0;
        for (int i = n - 1; i >= n - i - 1; i--) {
            int up = 0;
            if (i == n - 1) up = 1;
            int tt = 0;
            tt = (k - (t % k)) % k;
            for (int j = 9; j >= up; j--) {
                if (is[i][j][tt]) {
                    ans[i] = ans[n - i - 1] = j + '0';
                    t = (t + a[i] * j) % k;
                    if (n - i - 1 != i) t = (t + a[n - i - 1] * j) % k;
                    break;
                }
            }
        }
        return ans;


    }
};
