class Solution {
public:
    string largestPalindrome(int n, int k) {
        vector<int>mx = {0,9,8,9,8,5,8,9,8,9};
        vector<int>one = {0,9,8,9,8,5,6,7,8,9};
        vector<int>two = {0,99,88,99,88,55,66,77,88,99};
        if(n==1){
            return to_string(one[k]);
        }else{
            if(n==2)return to_string(two[k]);
        }
        string s;
        vector<int>ans(n,0);
        ans[0] = mx[k];
        ans[n-1] = mx[k];
        if(k==1||k==2||k==3||k==5||k==9){
            for(auto x:ans)s+=to_string(x!=0?x:9);
            return s;
        }
        if(k==4){
            ans[1] = 8;ans[n-2] = 8;
            for(auto x:ans)s+=to_string(x!=0?x:9);
            return s;
        }
        if(k==8){
            ans[1] = 8;ans[n-2] = 8;
            ans[2] = 8;ans[n-3] = 8;
            for(auto x:ans)s+=to_string(x!=0?x:9);
            return s;
        }
        int m = n/2;
        if(n%2==0)m--;
        for(int i = 1;i<m;i++){
            ans[i] = 9;
            ans[n-i-1] = 9;
        }
        vector<int>mod(10,0);
        for(int j = 0;j<10;j++){
            for(int i = 0;i<n;i++){
                mod[j]=mod[j]*10+(ans[i]!=0?ans[i]:j);
                mod[j]%=k;
            }
        }
        for(int i = 9;i>=0;i--){
            if(mod[i]==0){
                if(n%2){
                    ans[n/2] = i;
                    break;
                }else{
                    ans[n/2] = i;
                    ans[n/2-1] = i;
                    break;
                }
            }
        }
        for(auto x:ans)s+=to_string(x);
        return s;
    }
};
