class Solution {
public:
    string largestPalindrome(int n, int k) {
        int rest[6] = { 1,3,2,6,4,5 };
        int tmp = 0;

        switch (k) {
        case 1:
            return string(n, '9');
        case 2:
            if (n <= 2) {
                return string(n, '8');
            }

            return '8' + string(n - 2, '9') + '8';
        case 3:
            return string(n, '9');
        case 4:
            if (n <= 4) {
                return string(n, '8');
            }

            return "88" + string(n - 4, '9') + "88";
        case 5:
            if (n <= 2) {
                return string(n, '5');
            }

            return '5' + string(n - 2, '9') + '5';
        case 6:
            if (n <= 2) {
                return string(n, '6');
            }

            if (n % 2 == 1) {
                return '8' + string((n - 3) / 2, '9') + '8' + string((n - 3) / 2, '9') + '8';
            }

            return '8' + string((n - 4) / 2, '9') + "77" + string((n - 4) / 2, '9') + '8';
        case 7:
            if (n <= 2) {
                return string(n, '7');
            }          

            if (n % 2 == 1) {
                tmp = (rest[n % 6] - 9 * rest[(n / 2) % 6] % 7 - 1 + 14) % 7;

                for (int i = 9; i >= 1; i--) {
                    if ((tmp + i * rest[(n / 2) % 6]) % 7 == 0) {
                        return string(n / 2, '9') + to_string(i) + string(n / 2, '9');
                    }
                }
            }

            tmp = (rest[n % 6] - 9 * (rest[(n / 2) % 6] + rest[(n / 2 - 1) % 6]) % 7 - 1 + 14) % 7;

            for (int i = 9; i >= 1; i--) {
                if ((tmp + i * (rest[(n / 2) % 6] + rest[(n / 2 - 1) % 6])) % 7 == 0) {
                    return string(n / 2 - 1, '9') + to_string(i) + to_string(i) + string(n / 2 - 1, '9');
                }
            }
        case 8:
            if (n <= 6) {
                return string(n, '8');
            }

            return "888" + string(n - 6, '9') + "888";
        case 9:
            return string(n, '9');
        }

        return "";
    }
};
