class Solution(object):
    def largestPalindrome(self, n, k):
        def tenpow(n):
            return (3 ** (n % 6)) % 7

        def inv(y):
            if y == 1:
                return 1
            elif y == 2:
                return 4
            elif y == 3:
                return 5
            elif y == 4:
                return 2
            elif y == 5:
                return 3
            elif y == 6:
                return 6
