                return "5"
            return "5" + "9" * (n - 2) + "5"
        if k == 2:
            if n == 1:
                return "8"
            return "8" + "9" * (n - 2) + "8"
        if k == 4:
            if n <= 3:
                return "8" * n
            else:
                return "88" + "9" * (n - 4) + "88"
        if k == 8:
