class Solution {
public:
    bool check(string &n, long long k) {
        long long rem = 0;
        for (auto i : n)
            rem = ((rem * 10) + i - '0') % k;
        return rem == 0;
    }
    string largestPalindrome(int n, int k) {
        string str;
        for(int i=0;i<n;i++)str+='9';
        if(k==1 || k==3 || k==9)return str;
        if(k==2){
            str[0]='8';str[n-1]='8';
            return str;
        }              
        if(k==5){
            str[0]='5';str[n-1]='5';
            return str;
        }
        if(k==4){
            str[0]='8';str[n-1]='8';
            if(n>1){str[1]='8';str[n-2]='8';}
            return str;
        }
        if(k==6){
            if(n==1)return "6";
            if(n==2)return "66";
            str[0]='8';str[n-1]='8';
            if(n%2==1){
                int mid=n/2;
                str[mid]='8';
                return str;
            }else {
                int mid=n/2;
                str[mid-1]='7';str[mid]='7';
                return str;
            }
        }
        if(k==8){
            if(n==1)return "8";
            if(n==2)return "88";
            if(n==3)return "888";
            if(n==4)return "8888";
            str[0]='8';str[n-1]='8';
            str[1]='8';str[n-2]='8';
            str[2]='8';str[n-3]='8';
            return str;
        }
        if(n==1)return "7";
        if(n%2){
            int mid=n/2;
            char d='8';
            while(!check(str,k)){
                str[mid]=d;
                d--;
            }
            return str;
        }
        else {
            int mid=n/2;
            char d='8';
            while(!check(str,k)){
                str[mid]=d;str[mid-1]=d;
                d--;
            }
            return str;
        }

    }
};
