class Solution {
public:

    bool div(string ans, int n){
        // cout<<ans<<" ";
        int c = 0;
        for(int i = 0;i<ans.length();i++){
            c = c*10 + (ans[i]-'0');
            c %= n;
        }
        return c == 0;
    }

    string func9(int n){
        string s;
        for(int i = 0;i<n;i++)      s += '9';
        return s;
    }

    string largestPalindrome(int n, int k) {

        if(k == 1)      return string(n,'9');

        if(k == 2){
            if(n<=2)        return string(n,'8');
            return "8"+string(n-2,'9')+"8";
        }

        if(k == 3 || k == 9)      return string(n,'9');


        if(k == 4){
            if(n<=4)            return string(n,'8');
            return "88" + string(n-4,'9') + "88";
        }

        if(k == 8){
            if(n<=6)            return string(n,'8');
            return "888" + string(n-6,'9') + "888";
        }

        if(k == 5){
            if(n<=2)        return string(n,'5');
            return "5"+func9(n-2)+"5";
        }

        if(k == 6){
            if(n<=2)        return string(n,'6');
            if(n%2 == 0){
                string t = "8"+ string(max(0,(n-4)/2),'9')+"7";
                string s = t;
                reverse(s.begin(),s.end());
                return t + s;
            }
            else{
                string t = "8" + string((n-3)/2, '9') + "8"+string((n-3)/2,'9')+"8";
                return t;
            }
        }

        if(n<=2)        return string(n,'7');

        string s = string((n-1)/2,'9');
        string ans = string(n,'0');
        if(n%2 == 1){
            for(char j = '0';j<='9';j++){
                if(div(s + j + s, 7))       ans = max(ans,s+j+s);
            }
        }
        else{
            for(char j = '0';j<='9';j++){
                if(div(s+j+j+s,7))      ans = max(ans,s+j+j+s);
            }
        }
        return ans;   
    }
};
