class Solution {
public:
    string largestPalindrome(int n, int k) {
        string s = "";
        for (int i=0; i<n; i++){
            s.push_back('9');
        }
        if (k==1 || k==3 || k==9) return s;
        if (k==2) {
            s[0] = '8';
            s[n-1] = '8';
            return s;
        }
        if (k==4) {
            s[0] = '8';
            s[n-1] = '8';
            if (n<3) return s;
            s[1] = '8';
            s[n-2] = '8';
            return s;
        }
        if (k==8){
            s[0] = '8';
            s[n-1] = '8';
            if (n<3) return s;
            s[1] = '8';
            s[n-2] = '8';
            if (n<5) return s;
            s[2] = '8';
            s[n-3] = '8';
            return s;
        }
        if (k==5){
            s[0] = '5';
            s[n-1] = '5';
            return s;
        }
        if (k == 6) {
            if (n==1) return "6";
            s[0] = '8';
            s[n-1] = '8';

            int digitSum = 16;
            for (int i = 1; i < n-1; i++) {
                digitSum += (s[i] - '0');
            }

            if (digitSum % 3 != 0) {
                if (n % 2 == 0) {
                    int adjust = 3 - (digitSum % 3);
                    s[n/2 - 1] = s[n/2 - 1] - adjust;
                    s[n/2] = s[n/2] - adjust;
                } else {
                    int adjust = digitSum % 3;
                    s[n/2] = s[n/2] - adjust;
                }
            }

            return s;
        }
        if (k == 7) {
            while (true) {
                int remainder = 0;
                for (int i = 0; i < n; i++) {
                    remainder = (remainder * 10 + (s[i] - '0')) % 7;
                }

                if (remainder == 0) {
                    return s;
                }

                for (int i = n / 2; i >= 0; i--) {
                    if (s[i] > '0') {
                        s[i]--;
                        s[n-i-1] = s[i];
                        break;
                    } else {
                        s[i] = s[n-i-1] = '9';
                    }
                }
            }
        }
        return "";
    }
};
