#define ll long long
class Solution {
public:
    ll Div_mod(string a,ll k){
        ll i, sum=0;
        for(ll i = 0; i < a.length(); i++) sum=(sum*10+(a[i]-'0'))%k;
        return sum;
    }
    string largestPalindrome(int n, int k) {

        string num(n, '9');
        if(k == 1 || k == 3 || k == 9) return num;

        if(k == 2){
            num[0] = num[n - 1] = '8';
            return num;
        }

        if(k == 4){
            if(n >= 4){
                num[0] = num[1] = num[n - 1] = num[n - 2] = '8';
            }
            else {
                num[0] = num[n - 1] = num[n / 2] = '8';
            }
            return num;
        }

        if(k == 5){
            num[0] = num[n - 1] = '5';
            return num;
        }
        if(k == 8){
            if(n >= 6){
                num[0] = num[1] = num[n - 1] = num[n - 2] = num[n - 3] = num[2] = '8';
            }else{
                num = string(n, '8');
            }
            return num;
        }
        if(k == 6){
            ll sum = 0;
            if(n == 1){
                num[0] = '6';
            }else if(n == 2){
                num[0] = num[n - 1] = '6';
            }else{
                num[0] = num[n - 1] = '8';
                if(n & 1){
                    num[n / 2] = '8';
                }else{
                    num[n/2 - 1] = num[n / 2] = '7';
                }
            }
            return num;
        }

        assert(k == 7);

        if(n & 1){
            ll mid = n / 2;
            for(char cc = '9'; cc >= 0; cc--){
                num[0] = num[n - 1] = cc;
                for(char c = '9'; c >= 0; c --){
                    num[mid] = c;
                    if(Div_mod(num, k) == 0){
                        return num;
                    }
                }
            }
        }
        else{
            ll mid = n / 2 - 1;
            for(char cc = '9'; cc >= 0; cc--){
                num[0] = num[n - 1] = cc;
                for(char c = '9'; c >= 0; c --){
                    num[mid] = num[mid + 1] = c;
                    if(Div_mod(num, k) == 0){
                        return num;
                    }
                }
            }
        }
        return num;
    }
};
