class Solution {
public:
 bool check(vector<char> pal){
    int n = pal.size();
    int x = 0;
    for(int i=0;i<n-1;++i){
        int v = pal[i]-'0';
        x = x*10 + v;
        x%=7;
    }
    return x==4;
 }
    string largestPalindrome(int n, int k) {
        vector<char> pal(n);
        string ans = "";
        if(k==1 || k==3 || k==9){
            string res = "";
            for(int i=0;i<n;++i){
                res+= '9';
            }
            return res;
        }
        if(k==2){
            pal[0] = pal[n-1] = '8';
            for(int i=1;i<n-1;++i) pal[i] = '9';
            for(int i=0;i<n;++i) ans+= pal[i];
        }
        if(k==4){
            if(n==1) return "8";
            pal[0] = pal[1] = pal[n-1] = pal[n-2]  = '8';
            for(int i=2;i<n-2;++i) pal[i] = '9';
            for(int i=0;i<n;++i) ans+= pal[i];
        }
        if(k==5){
            pal[0] = pal[n-1] = '5';
            for(int i=1;i<n-1;++i) pal[i] = '9';
            for(int i=0;i<n;++i) ans+= pal[i];
        }
        if(k==8){
            if(n==1) return "8";
            if(n==2) return "88";
            pal[0] = pal[1] = pal[2] = pal[n-1] = pal[n-2] = pal[n-3] = '8';
            for(int i=3;i<n-3;++i) pal[i] = '9';
            for(int i=0;i<n;++i) ans+= pal[i];
        }
        if(k==6){
            if(n==1) return "6";
            if(n==2) return "66";
            if(n==3) return "888";
            if(n==4) return "8778";
            pal[0] = pal[n-1] = '8';
            if(n&1){
                pal[n/2] = '8';
                for(int i=1;i<n/2;++i){
                    pal[i] = pal[n-i-1] = '9';
                }
                for(int i=0;i<n;++i) ans+= pal[i];
            }
            else{
                pal[n/2] = pal[n/2 -1] = '7';
                for(int i=1;i<n/2-1;++i){
                    pal[i] = pal[n-i-1] = '9';
                }
                for(int i=0;i<n;++i) ans+= pal[i];
            }
        }
        if(k==7){
            if(n==1) return "7";
            if(n==2) return "77";
            if(n==3) return "959";
            if(n==4) return "9779";
            for(int i=0;i<n;++i) pal[i] = '9';
            if(n&1){
                for(int i=9;i>=0;--i){
                    pal[n/2] = i+'0';
                    if(check(pal)){break;}
                }
            }
            else{
                for(int i=9;i>=0;--i){
                    pal[n/2] = pal[n/2-1] = i+'0';
                    if(check(pal)){break;}
                }
            }
            for(int i=0;i<n;++i) ans+= pal[i];
        }

        return ans;
    }
};
