#include <vector>
#include <string>
#include <algorithm>

using namespace std;

class Solution {
public:
    char dp[100001][7]; // Dynamic programming table for palindrome construction
    char dp_non_zero[100001][7]; // DP table for non-zero digits
    int next_mod[100001][7]; // Tracking next modulus for palindromes
    int next_non_zero_mod[100001][7]; // Tracking next modulus for non-zero palindromes

    string largestPalindrome(int n, int k) {
        // Handle specific cases based on k
        if (k == 1 || k == 3 || k == 9) {
            return string(n, '9');
        }

        if (k == 2) {
            if (n == 1) {
                return "8";
            }
            string result(n, '9');
            result[0] = '8';
            result[n - 1] = '8';
            return result;
        }

        if (k == 5) {
            if (n == 1) {
                return "5";
            }
            string result(n, '9');
            result[0] = '5';
            result[n - 1] = '5';
            return result;
        }

        if (k == 4) {
            if (n <= 3) {
                return string(n, '8');
            }
            string result(n, '9');
            result[0] = '8';
            result[n - 1] = '8';
            result[1] = '8';
            result[n - 2] = '8';
            return result;
        }

        if (k == 8) {
            if (n <= 5) {
                return string(n, '8');
            }
            string result(n, '9');
            result[0] = '8';
            result[n - 1] = '8';
            result[1] = '8';
            result[n - 2] = '8';
            result[2] = '8';
            result[n - 3] = '8';
            return result;
        }

        if (k == 6) {
            if (n == 1) {
                return "6";
            }
            if (n == 2) {
                return "66";
            }
            if (n == 3) {
                return "888";
            }
            string result(n, '9');
            result[0] = '8';
            result[n - 1] = '8';
            if (n % 2 == 1) {
                result[n / 2] = '8';
            } else {
                result[n / 2] = '7';
                result[(n / 2) - 1] = '7';
            }
            return result;
        }

        if (k == 7) {
            if (n == 1) {
                return "7";
            }

            fill(&dp[0][0], &dp[0][0] + sizeof(dp), 0);
            fill(&dp_non_zero[0][0], &dp_non_zero[0][0] + sizeof(dp_non_zero), 0);

            // Initialize DP tables
            for (int digit = 1; digit < 10; ++digit) {
                dp[1][digit % 7] = '0' + digit;
                if (digit != 0) {
                    dp_non_zero[1][digit % 7] = '0' + digit;
                }
            }

            int base_mod = 10 % 7;
            for (int length = 0; length <= 100000 - 2; ++length) {
                for (int mod = 0; mod < 7; ++mod) {
                    if ((length == 0 && mod != 0) || (length > 0 && dp[length][mod] == 0)) {
                        continue;
                    }
                    for (int digit = 9; digit >= 0; --digit) {
                        int new_mod = (base_mod * digit + digit + mod * 10) % 7;
                        if (dp[length + 2][new_mod] == 0 || dp[length + 2][new_mod] < '0' + digit) {
                            dp[length + 2][new_mod] = '0' + digit;
                            next_mod[length + 2][new_mod] = mod;
                        }
                        if (digit == 0) continue;
                        if (dp_non_zero[length + 2][new_mod] == 0 || dp_non_zero[length + 2][new_mod] < '0' + digit) {
                            dp_non_zero[length + 2][new_mod] = '0' + digit;
                            next_non_zero_mod[length + 2][new_mod] = mod;
                        }
                    }
                }
                base_mod = (base_mod * 10) % 7;
            }

            int remaining_length = n;
            string front(1, dp_non_zero[remaining_length][0]);
            string back(1, dp_non_zero[remaining_length][0]);
            int mod = next_non_zero_mod[remaining_length][0];
            remaining_length -= 2;
            while (remaining_length > 0) {
                front += dp[remaining_length][mod];
                if (remaining_length > 1) back += dp[remaining_length][mod];
                else break;
                mod = next_mod[remaining_length][mod];
                remaining_length -= 2;
            }

            reverse(back.begin(), back.end());
            return front + back;
        }

        return "";
    }
};
