class Solution {
public:
    bool div(int i, string s) {
    int n = s.length();

    while (i < n) {
        if (i == 0) {
            if (s[i] < '7') {
                i++;
                continue;
            } else {
                s[i] -= 7;
                i++;
                continue;
            }
        }

        int num = (s[i-1] - '0') * 10 + (s[i] - '0');
        num %= 7;
        s[i] = ('0' + num);
        s[i-1] = '0';

        i++;
    }

    return (s[n-1] < '7' && s[n-1] != '0');
}

    string largestPalindrome(int n, int k) {
        string ans="";
        if(k==1 || k==3 || k==9){
            for(int i=0; i<n; i++){
                ans+='9';
            }
        }else if(k==2){
            if(n==1){
                ans="8";
            }else if(n==2){
                ans="88";
            }else{
                ans+='8';
                for(int i=0; i<n-2; i++){
                    ans+='9';
                }
                ans+='8';
            }
        }else if(k==4){
            if(n==1){
                ans="8";
            }else if(n==2){
                ans="88";
            }else if(n==3){
                ans="888";
            }else{
                ans+="88";
                for(int i=0; i<n-4; i++){
                    ans+='9';
                }
                ans+="88";
            }
        }else if(k==5){
            if(n==1){
                ans="5";
            }else if(n==2){
                ans="55";
            }else{
                ans+='5';
                for(int i=0; i<n-2; i++){
                    ans+='9';
                }
                ans+='5';
            }
        }else if(k==6){
            if(n==1){
                ans="6";
            }else if(n==2){
                ans="66";
            }else if(n==3){
                ans="888";
            }else{
                int s=16;
                ans+="8";
                for(int i=0; i<n-2; i++){
                    ans+='9';
                    s+=9;
                }
                ans+="8";
                if(n%2){
                    ans[n/2]-=(s%6);
                }else{
                    ans[n/2]-=(s%6)/2;
                    ans[n/2-1]-=(s%6)/2;
                }
            }
        }else if(k==7){
            if(n==1){
                ans="7";
            }else if(n==2){
                ans="77";
            }else{
                for(int i=0; i<n; i++){
                    ans+='9';
                }
                while(div(0,ans)){
                    if(n%2){
                        ans[n/2]--;
                    }else{
                        ans[n/2]--;
                        ans[n/2-1]--;
                    }
                }
            }
        }else if(k==8){
            if(n==1){
                ans="8";
            }else if(n==2){
                ans="88";
            }else if(n==3){
                ans="888";
            }else if(n==4){
                ans="8888";
            }else if(n==5){
                ans="88888";
            }else{
                ans+="888";
                for(int i=0; i<n-6; i++){
                    ans+='9';
                }
                ans+="888";
            }
        }
        return ans;
    }
};
