class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if n <= 4:
            size = (n+1)//2
            for num in range(10**size-1, -1, -1):
                if n%2==0:
                    pal = str(num)+str(num)[::-1]
                else:
                    pal = str(num)+str(num)[:-1][::-1]
                if int(pal)%k==0:
                    return pal
        else:
            size = (n+1)//2
            if k in [1, 3, 9]: start = '9'*size
            elif k in [2]: start = '8'+'9'*(size-1)
            elif k in [4]: start = '88'+'9'*(size-2)
            elif k in [5]: start = '5'+'9'*(size-1)
            elif k in [8]: start = '888'+'9'*(size-3)
            elif k in [6]:
                if n%2==0:
                    start = '8'+'9'*(size-2)+'7'
                else:
                    start = '8'+'9'*(size-2)+'8'
            elif k in [7]:
                remain = [1,3,3,5,3,3,1,6,6,4,6,6]
                start = '9'*(size-1)+str(10-remain[n%12])
            # print(start)
            if n%2==0:
                return start+start[::-1]
            else:
                return start+start[:-1][::-1]
        return -1
