class Solution {
public:

    char arr1[100001][7];
    char arr2[100001][7];
    int arr3[100001][7];
    int arr4[100001][7];

    bool isPalindrome(string s){
        int n = s.size();
        for(int i=0; i<n/2; i++){
            if(s[i] != s[n-i-1]){
                return false;
            }
        }
        return true;
    }

    bool isPrime(int n){
        for(int i=2; i*i<=n; i++){
            if(n % i == 0){
                return false;
            }
        }
        return true;
    } 

    string checkAns(int n, int k){
        if(n != 1){
            memset(arr1, 0, sizeof(arr1));
            memset(arr2, 0, sizeof(arr2));

            for(int i=1; i<10; i++){
                arr1[1][i % 7] = '0' + i;
                if(i != 0){
                    arr2[1][i % 7] = '0' + i;
                }
            }

            int base = 10 % 7;
            for(int i=0; i<=1e5-2; i++){
                for(int j=0; j<7; j++){
                    if((i==0 && j != 0) || (i > 0 && arr1[i][j]==0)){
                        continue;
                    }
                    for(int k=9; k>=0; k--){
                        int nBase = (base * k + k + j * 10) % 7;
                        if(arr1[i+2][nBase]==0 || arr1[i+2][nBase] < '0' + k){
                            arr1[i+2][nBase] = '0' + k;
                            arr3[i+2][nBase] = j;
                        }
                        if(k==0){
                            continue;
                        }
                        if(arr2[i+2][nBase]==0 || arr2[i+2][nBase]<'0' + k){
                            arr2[i+2][nBase] = '0' + k;
                            arr4[i+2][nBase] = j;
                        }
                    }
                }
                base = (base * 10) % 7;
            }
            int val = n;
            string s1(1, arr2[val][0]);
            string s2(1, arr2[val][0]);
            int tmp = arr4[val][0];
            val = val - 2;
            while(val){
                s1 += arr1[val][tmp];
                if(val > 1){
                    s2 += arr2[val][tmp];
                }else{
                    break;
                }
                tmp = arr3[val][tmp];
                val -= 2;
            }
            reverse(s2.begin(),s2.end());
            string ans = s1 + s2;
            return ans;
        }
        return "7";
    }

    string getAns(int n, int k){
        if(k==1 || k==3 || k==9){
            string ans = "";
            for(int i=0; i<n; i++){
                ans.pb('9');
            }
            return ans;
        }else if(k==2){
            if(n==1){
                return "8";
            }else{
                string ans = "";
                for(int i=0; i<n; i++){
                    ans.pb('9');
                }
                ans[0] = '8';
                ans[n-1] = '8';
                return ans;
            }
        }else if(k==4){
            string ans = "";
            if(n<=3){
                for(int i=0; i<n; i++){
                    ans.pb('8');
                }
                return ans;
            }else{
                for(int i=0; i<n; i++){
                    ans.pb('9');
                }
                ans[0] = '8';
                ans[n-1] = '8';
                ans[1] = '8';
                ans[n-2] = '8';
                return ans;
            }
        }else if(k==5){
            if(n==1){
                return "5";
            }else{
                string ans = "";
                for(int i=0; i<n; i++){
                    ans.pb('9');
                }
                ans[0] = '5';
                ans[n-1] = '5';
                return ans;
            }
        }else if(k==6){
            if(n==1){
                return "6";
            }else if(n==2){
                return "66";
            }else if(n==3){
                return "888";
            }else{
                string ans = "";
                for(int i=0; i<n; i++){
                    ans.pb('9');
                }
                ans[0] = '8';
                ans[n-1] = '8';
                if(n & 1){
                    ans[n/2] = '8';
                }else{
                    ans[n/2] = '7';
                    ans[(n/2) - 1] = '7';
                }
                return ans;
            }
        }else if(k==7){
            string ans = checkAns(n,k);
            return ans;
        }else if(k==8){
            string ans = "";
            if(n <= 5){
                for(int i=0; i<n; i++){
                    ans.pb('8');
                }
                return ans;
            }else{
                for(int i=0; i<n; i++){
                    ans.pb('9');
                }
                ans[0] = '8';
                ans[n-1] = '8';
                ans[1] = '8';
                ans[n-2] = '8';
                ans[2] = '8';
                ans[n-3] = '8';
                return ans;
            }
        }
        return "";
    }

    string largestPalindrome(int n, int k) {
        string ans = getAns(n,k);
        return ans;
    }
};

