class Solution {
public:
    bool isDivisibleBy7(const string& s) {
        int remainder = 0;
        for (char c : s) {
            remainder = (remainder * 10 + (c - '0')) % 7;
        }
        return remainder == 0;
    }

    void decrementHalf(string &half) {
        int i = half.length() - 1;
        while (i >= 0) {
            if (half[i] > '0') {
                half[i]--;
                break;
            } else {
                half[i] = '9';
                i--;
            }
        }
    }

    string largestPalindrome(int n, int k) {
        string result = "";
        if (k == 1 || k == 3 || k == 9) {
            for (int i = 0; i < n; i++) result += '9';
            return result;
        }
        else if (k == 2) {
            result += '8';
            if (n == 1) return result;
            for (int i = 0; i < n - 2; i++) {
                result += '9';
            }
            result += '8';
            return result;
        }
        else if (k == 4) {
            result += '8';
            if (n == 1) return result;
            else if (n == 2) return "88";
            else if (n == 3) return "888";
            else if(n==4) return "8888";
            result += '88';
            for (int i = 0; i < n - 4; i++) {
                result += '9';
            }
            result += "88";
            return result;
        }
        else if (k == 5) {
            result += "5";
            if (n == 1) return result;
            for (int i = 0; i < n - 2; i++) {
                result += '9';
            }
            result += "5";
            return result;
        }
        else if (k == 6) {
            if (n == 1) return "6";
            else if (n == 2) return "66";
            else if(n==3) return "888";
            else if(n==4) return "8778";
            result += '8';
            if (n % 2 != 0) {
                for (int i = 0; i < n - 2; i++) {
                    if (i != (n - 2) / 2) result += '9';
                    else result += '8';
                }
                result += '8';
                return result;
            }
            else {
                for (int i = 0; i < n - 2; i++) {
                    if ((i != (n - 2) / 2) && (i != (n-4) / 2)) result += '9';
                    else result += '7';
                }
                result += '8';
                return result;
            }
        }
        else if (k == 8) {
            if (n <= 6) {
                for (int i = 0; i < n; i++) {
                    result += '8';
                }
                return result;
            }
            else {
                result += "888";
                for (int i = 0; i < n - 6; i++) {
                    result += '9';
                }
                result += "888";
                return result;
            }
        }
        else {
            string half = "";
            for (int i = 0; i < (n + 1) / 2; i++) {
                half += '9';
            }

            string palindrome = half + string(half.rbegin() + (n % 2), half.rend());
            if (isDivisibleBy7(palindrome)) {
                return palindrome;
            }

            while (true) {
                decrementHalf(half);
                palindrome = half + string(half.rbegin() + (n % 2), half.rend());
                if (isDivisibleBy7(palindrome)) {
                    return palindrome;
                }
            }
        }
    }
};
