class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans = "";
        int i;
        if (k == 1 || k == 9 || k == 3) {
            for (i = 0; i < n; i++) {
                ans += "9";
            }
        } else if (k == 2) {
            ans += "8";
            for (i = 1; i < n-1; i++) {
                ans += "9";
            }
            if (n > 1) {
                ans += "8";
            }
        } else if (k == 5) {
            ans += "5";
            for (i = 1; i < n-1; i++) {
                ans += "9";
            }
            if (n > 1) {
                ans += "5";
            }
        } else if (k == 6) {
            if (n == 1) {
                ans = "6";
            } else if (n == 2) {
                ans = "66";
            } else {
                int sum = (n-1)/2*2 * 9;
                sum -= 2;
                int d = 9;
                int left = n - (n-1)/2*2;
                while ((sum + left * d) %3 != 0) {
                    d--;
                }
               // cout<<d<<endl;
                ans = "8";
                for (i = 1; i < (n-1); i++) {
                    ans += "9";
                }
                ans += "8";
                for (i = (n-1)/2; i <= n/2; i++) {
                    ans[i] = char(d + '0');
                }
            }
        }
        else if (k == 4) {
            if (n <= 4) {
                for (i = 0; i < n; i++) {
                    ans += "8";
                }
            } else {
                ans = "88";
                for (i = 2; i < n-2; i++) {
                    ans += "9";
                }
                ans += "88";
            }
        } else if (k == 8) {
            if (n <= 6) {
                for (i = 0; i < n; i++) {
                    ans += "8";
                }
            } else {
                ans = "888";
                for (i = 3; i < n-3; i++) {
                    ans += "9";
                }
                ans += "888";
            }
        } else if (k == 7) {
            long long ansx = 0;
            int lt = n % 12;
            for (i = 0; i < lt; i++) {
                ansx = ansx * 10ll + 9ll;
            }
            long long tx = 1;
            if (lt%2) {
                for (i = 0; i < (lt-1) / 2; i++) {
                    tx = tx * 10ll;
                }
            } else {
                tx = 11;
                for (i = 0; i < (lt-1) / 2; i++) {
                    tx = tx * 10ll;
                }
            }
            int d = 0;
            while (ansx % 7 != 0) {
                ansx -= tx;
                d++;
            }
            int s[13] = {};
            int dx = lt - 1;
            while (ansx != 0) {
                s[dx] = ansx % 10;
                ansx /=10;
                dx--;
            }
            for (i = 0; i < n/12*6; i++) {
                ans += "9";
            }
            for (i = 0; i < lt; i++) {
                ans += char(s[i] +'0');
            }
            for (i = 0; i < n/12*6; i++) {
                ans += "9";
            }
        }
        return ans;
    }
};
