import sys
class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        sys.set_int_max_str_digits(100000)
        if k == 1 or k == 3 or k == 9:
            return '9'*n

        elif k == 2:
            if n == 1:
                return '8'*n
            return '8' + '9'*(n-2) + '8'

        elif k == 4:
            if n <= 4:
                return '8'*n
            return '88' + '9'*(n-4) + '88'

        elif k == 5:
            if n == 1:
                return '5'
            return '5' + '9'*(n-2) + '5'

        elif k == 6:
            if n <= 2:
                return '6'*n
            m = n//2
            if n%2 == 0:
                return '8'+'9'*(m-2)+'77'+'9'*(m-2)+'8'
            else:
                return '8'+'9'*(m-1)+'8'+'9'*(m-1)+'8'

        elif k == 7:
            if n <= 2:
                return '7'*n
            r = int('9'*n)%7

            m = n//2
            if n%2 == 0:
                for digit in range(9):
                    num = int(f'{digit}{digit}'+'0'*(m-1))
                    if r-num%7 == 0:
                        return '9'*(m-1)+f'{9-digit}{9-digit}'+'9'*(m-1)
            else:
                for digit in range(9):
                    num = int(f'{digit}'+'0'*m)
                    if r-num%7 == 0:
                        return '9'*m+f'{9-digit}'+'9'*m 

        elif k == 8:
            if n <= 6:
                return '8'*n
            return '888'+'9'*(n-6)+'888'
